import React, {useContext, Component, useEffect} from 'react';
import './Appearence/index.css'
import {AuthContext} from "./Context/AuthContext";
import {useHistory} from "react-router-dom";

export const Header = () => {

    const history = useHistory()
    const {token, logout} = useContext(AuthContext)

    const logoutHandler = e => {
      e.preventDefault()
      logout()
      history.push('/')
    }

    useEffect(() => {
        if (!localStorage.getItem('userData').token)
            // logoutHandler(null)
            // logout()
            console.log('no tokeeen')
    })

    return (
        <div className = 'headerZone'>
          <div className = 'logoutbtn' onClick = {e => logoutHandler(e)}><a href = '/'/></div>
        </div>
    );
}
  export default Header;
