import * as cyrToTranslit from 'cyrillic-to-translit-js'
import {createContext, useContext} from "react";
import {RegExps} from './RegExps'

// const {punctuations} = useContext(RegExps)

const createShortLink = name => {
    const shortLink = cyrToTranslit().transform(name).toString()
    // link.replace(punctuations, '-')
    shortLink.replace(/[^А-Яа-яA-Za-z]*/gi, '-')
    console.log(shortLink, 'трансформировано')
    return shortLink
}

export const Translit = createContext({
    createShortLink: createShortLink
})
