import React, {useContext} from 'react';
import './Appearence/Profile.css'
import pic from '../Images/ProfilePic.png';
import './Appearence/index.css'
import NavigationBar from "./NavigationBar";
import {AuthContext} from "./context/AuthContext";
import {useAuth} from "../hooks/auth.hook";

export const Profile = () => {

  const {name, surname, username, userType} = useContext(AuthContext)

    return (
      <div>
        <div className = 'profileContainer'>
          <h1>{name}{surname} <div className = 'login'>{username}</div></h1>
          {userType === 'a' &&
            <h2>Администратор</h2>}
          {userType === 'p' &&
            <h2>Педагог-психолог</h2>}
          {userType === 'u' &&
            <h2>Ученик</h2>}

          <div className = 'circlePic'>
            <img src = {pic} className = 'profilePic'/>
          </div>
        </div>
        {/*<ComponentsPanel/>*/}
      </div>
    )


}

export default Profile;
