import React, {useContext, useEffect, useState} from 'react';
import './Appearence/App.css';
import './Appearence/Auth.css';
import {useHttp} from "../hooks/http.hook";
import {useMessage} from "../hooks/message.hook";
import {AuthContext} from "./context/AuthContext";

export const Auth = () => {
    const auth = useContext(AuthContext)
    const {loading, request, error, clearError} = useHttp();
    const [form, setForm] = useState({ email: '', password: '' })
    const message = useMessage()

    useEffect(() => {
        message(error)
        clearError()
    }, [error, message, clearError])

     const changeHandler = event => {
         setForm({...form, [event.target.name]: event.target.value})
     }

     const registerHandler = async () => {
         try {
          const data = await request('api/auth/register', 'POST', {...form})
             console.log(data.message)
         }
         catch (e) {}
     }

    const loginHandler = async () => {
        try {
            const data = await request('api/auth/login', 'POST', {...form})
            console.log(data.message)
            auth.login(data.token, data.userID)
        }
        catch (e) {}
    }


      return (
          <div className='authPage'>
              <div className = 'form'>
                  <input type='text' name='email' placeholder='Логин' onChange={changeHandler}/>
                  <input type='password' name='password' placeholder='Пароль' onChange={changeHandler}/>
                  <div className = 'loginbtn' onClick = {loginHandler}/>
              </div>
              {/*<input type='button' value='Войти' onClick = {registerHandler} disabled={loading}/>*/}
              {/*<input type='button' className = 'loginbtn' value='go' onClick = {loginHandler} disabled={loading}/>*/}

          </div>
      )
}

export default Auth
