import React, {Component} from 'react';
import './Appearence/App.css';
import './Appearence/Auth.css';

class Auth extends React.Component {
   constructor(props) {
       super(props);
       this.state = {login: '', password: '', logged: false};

       this.handleChangeLogin = this.handleChangeLogin.bind(this);
       this.handleChangePassword = this.handleChangePassword.bind(this);
       this.handleSubmit = this.handleSubmit.bind(this);
   }

   handleChangeLogin(event){
       this.setState({login: event.target.value});
   }
    handleChangePassword(event){
        this.setState({password: event.target.value});
    }
    handleSubmit(event){
        event.preventDefault();
        if (this.state.logged){
           alert('Вы ввели логин: '+ this.state.login + ' и пароль: ' + this.state.password);
       } else alert ('Вы ничего не ввели');
    }


  render() {
    return (
      <div class = 'authPage'>
          <img src = '/Users/alisakrylova/Intro/client/src/components/Appearence'/>
          <h1>Email</h1>
          <input type = 'text' id = 'email' name = 'email' placeholder = 'Логин' value = {this.state.login} onChange = {this.handleChangeLogin}></input>
          <h1>Пароль</h1>
          <input type = 'password' id = 'password' name  = 'password' placeholder = 'Пароль' value = {this.state.password} onChange = {this.handleChangePassword}></input>
          <input type = 'submit' value='Войти'></input>
      </div>
    )
  }
}

export default Auth;
