import React, {useCallback, useContext, useEffect, useState} from 'react';
import './Appearence/index.css'
import './Appearence/Auth.css'
import './Appearence/Students.css';
import './Appearence/DatePicker.css';
import {useHttp} from "../Hooks/http.hook";
import {AuthContext} from "./Context/AuthContext";
import {emojis, emojiToString} from "./Functions and const/Emoji";
import {RegExps} from "./Functions and const/RegExps";
import {NavLink} from "react-router-dom";
import DatePicker from "react-datepicker/es";
import {registerLocale} from "react-datepicker";
import * as cyrToTranslit from 'cyrillic-to-translit-js'
import formatDateToRu from "./Functions and const/DateFormat";
import notify from './Functions and const/Notify'
import showError from './Functions and const/ShowError'


export const Students = () => {

    const {loading, request, error, clearError} = useHttp();

    const [form, setForm] = useState({
        login: '',
        password: '',
        type: 's',
        pic: [128522],
        name: '',
        surname: '',
        grade: '',
        letter: '',
        birthday: ''
    })
    const [errors, setErrors] = useState({
        name: '',
        surname: '',
        login: '',
        password: ''
    })
    const [grades, setGrades] = useState([])
    const [shownGrades, setShownGrades] = useState([])
    const [search, setSearch] = useState([])
    const [notFound, setNotFound] = useState()
    const [sorted, setSorted] = useState(false)
    const [isSearching, activateSearching] = useState('none')
    const [isAdding, activateAdding] = useState('none')
    const [isChoosingEmoji, activateChoosingEmoji] = useState(false)
    const [date, setDate] = useState()
    const {token} = useContext(AuthContext)
    const {cyrillicsRegExp, loginRegExp, passwordRegExp } = useContext(RegExps)


    const months = ['–Ø–Ω–≤–∞—Ä—å', '–§–µ–≤—Ä–∞–ª—å', '–ú–∞—Ä—Ç', '–ê–ø—Ä–µ–ª—å', '–ú–∞–π', '–ò—é–Ω—å', '–ò—é–ª—å', '–ê–≤–≥—É—Å—Ç', '–°–µ–Ω—Ç—è–±—Ä—å', '–û–∫—Ç—è–±—Ä—å', '–ù–æ—è–±—Ä—å', '–î–µ–∫–∞–±—Ä—å']
    const days = ['–≤—Å', '–ø–Ω', '–≤—Ç', '—Å—Ä', '—á—Ç', '–ø—Ç', '—Å–±']

    registerLocale('ru', {
        localize: {
            month: i => months[i],
            day: i => days[i]
        },
        formatLong:{}
    });

    const changeHandler = e => {
        e.preventDefault()
        let login = form.login
        switch (e.target.name) {
            case 'grade':
                let thisYear = new Date().getFullYear();
                let then = new Date(thisYear - 7 - e.target.value, 0, 1);
                setDate(then);
                break
            case 'surname':
                login = cyrToTranslit().transform(e.target.value).toString().toLowerCase();
                break;
            case 'login':
                login = e.target.value.toString().toLowerCase();
                break;
        }
        setForm({...form, [e.target.name]: e.target.value, login: login, birthday: date})
    }


    const checkFields = async (e) => {
        e.preventDefault()
        try {
            if (!(form.name && form.surname && form.grade && form.letter && form.birthday && form.login && form.password))
                notify("–ù–µ –≤—Å–µ –ø–æ–ª—è –∑–∞–ø–æ–ª–Ω–µ–Ω—ã")
            // else {
            let name = '', surname = '', login = '', password = ''
            if (form.name && !cyrillicsRegExp.test(form.name)) {
                name = '–î–æ–ø—É—Å—Ç–∏–º–∞ —Ç–æ–ª—å–∫–æ –∏–∑ –∫–∏—Ä–∏–ª–ª–∏—Ü–∞'
                await showError(true,'name', 'errorName')
            }
            if (form.surname && !cyrillicsRegExp.test(form.surname)) {
                surname = '–î–æ–ø—É—Å—Ç–∏–º–∞ —Ç–æ–ª—å–∫–æ –∏–∑ –∫–∏—Ä–∏–ª–ª–∏—Ü–∞'
                await showError(true,'surname', 'errorSurname')
            }
            if (form.login && !loginRegExp.test(form.login)) {
                login = '–î–æ–ø—É—Å—Ç–∏–º—ã —Ç–æ–ª—å–∫–æ a-z, —Ü–∏—Ñ—Ä—ã, —Ç–æ—á–∫–∞ –∏ –ø—Ä–æ–±–µ–ª'
                await showError(true,'login', 'errorLogin')
            }

            if (form.password && !passwordRegExp.test(form.password)) {
                if (form.password.length < 6 || form.password.length > 20)
                    password = '–ü–∞—Ä–æ–ª—å –¥–æ–ª–∂–µ–Ω —Å–æ–¥–µ—Ä–∂–∞—Ç—å –æ—Ç 6 –¥–æ 20 —Å–∏–º–≤–æ–ª–æ–≤'
                else
                    password = '–î–æ–ø—É—Å—Ç–∏–º—ã —Ç–æ–ª—å–∫–æ A-z, —Ü–∏—Ñ—Ä—ã, —Ç–æ—á–∫–∞ –∏ –ø—Ä–æ–±–µ–ª'
                await showError(true, 'password', 'errorPassword')
            }

            !name && await showError(false,'name', 'errorName')
            !surname && await showError(false,'surname', 'errorSurname')
            !login && await showError(false,'login', 'errorLogin')
            !password && await showError(false,'password', 'errorPassword')

            setErrors({name, surname, login, password})

            // }
            // else register()
        }
        catch (e) {}
    }

    const registerHandler = async () => {
        try {
            const data = await request('students/create', 'POST', {...form})
            console.log(data.message)
            if (data.message === 'User\'ve created'){
                setForm({
                    login: '',
                    password: '',
                    type: 's',
                    pic: 128522,
                    name: '',
                    surname: '',
                    grade: '',
                    letter: '',
                    birthday: ''
                })
                // handleAddActivating()
            }
            else if (data.message === 'This user already  exists'){
                notify('–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å —Å —Ç–∞–∫–∏–º –ª–æ–≥–∏–Ω–æ–º —É–∂–µ —Å—É—â–µ—Å—Ç–≤—É–µ—Ç)')
            }
        }
        catch (e) {}
    }

    const fetchGrades = useCallback(async () => {
        try {
            const fetched = await request('/students', 'GET', null, {
                Authorization: `Bearer ${token}`
            })
            fetched.sort((a, b) => a.grade > b.grade ? 1 : -1);
            setGrades(fetched)

        } catch (e) {}
    }, [token, request])

    const handleSearchActivating = e => {
        e.preventDefault()
        switch (isSearching){
            case 'active':
                activateSearching('inactive');
                break
            case 'none':
            case 'inactive':
                activateSearching('active');
                document.getElementById('search').focus()
                break
        }
    }

    const doSearch = e => {
        e.preventDefault()
        try {
            let searchFilter = e.target.value.toString().toLowerCase()
            setSearch(searchFilter)
            setNotFound(false)
            if (searchFilter != '') {
                let gradesArray = []
                grades.map(grade => {
                    let studentsArray = []
                    grade.students.map(student => {
                        studentsArray.push((student.name + student.surname).toString().toLowerCase())
                    })
                    gradesArray.push((grade.grade + grade.letter + studentsArray).toString().toLowerCase())
                })

                let indexes = []
                gradesArray.map((grade, i) => {
                    if (grade.toString().includes(searchFilter))
                        indexes.push(i)
                })

                if (indexes.length != 0){
                    setShownGrades([...indexes])
                    setNotFound(false)
                }
                else setNotFound(true)
            }
            else showAll()
        }
        catch (e) {}
    }

    const doSort = e => {
        e.preventDefault()
        try {
            switch (sorted) {
                case true:
                    document.getElementById('sortIcon').style = 'transform: rotate(360deg);';
                    document.getElementById('gradesCollection').style = 'flex-direction: column;';
                    setSorted(false);
                    break;
                case false:
                    document.getElementById('sortIcon').style = 'transform: rotate(180deg);';
                    document.getElementById('gradesCollection').style = 'flex-direction: column-reverse;';
                    setSorted(true);
                    break;
            }
        }
        catch (e) {}
    }

    const handleAddActivating = e => {
        e.preventDefault()
        switch (isAdding){
            case 'active':
                activateAdding('inactive');
                document.getElementById('label').className = 'addingPanelAppearing'
                document.getElementById('gradesCollection').className = 'addingPanelAppearing gradesCollection'
                document.getElementById('addingStudent').className = 'gradesDisappearing addingStudent'
                break
            case 'none':
            case 'inactive':
                activateAdding('active');
                document.getElementById('label').className = 'addingPanelDisappearing'
                document.getElementById('gradesCollection').className = 'addingPanelDisappearing gradesCollection'
                document.getElementById('addingStudent').className = 'gradesAppearing addingStudent'
                break
        }
    }

    const handleChooseEmojiActivating = e => {
        e.preventDefault()
        activateChoosingEmoji(!isChoosingEmoji)
    }

    const setFormPic = e => {
        e.preventDefault()
        setForm({...form, pic: emojis[e.target.id.slice(1)]})
    }

    const showAll = () => {
        let shownSize = []
        for (let i = 0; i < grades.length; i++){
            shownSize.push(i)
        }
        setShownGrades([...shownSize])
    }

    useEffect(() => {

        if (grades.length == 0) fetchGrades()
        document.body.style = 'transition-duration: .5s; transition-timing-function: ease-in-out; background: white;'
        // console.log('üòä'.codePointAt())
        // console.log(String.fromCodePoint(128522))

        if (shownGrades.length == 0)
            showAll()

    }, [fetchGrades, shownGrades, showAll])




    return(
        <div>
            <h1 id = 'label' className = 'soft_appearing'>–£—á–µ–Ω–∏–∫–∏</h1>
            <div className = 'searchSortAndAdd'>
                <input type = 'text' id = 'search'
                       className = {  isSearching === 'active' && 'searchAppearing' ||
                       isSearching === 'inactive' && 'searchDisappearing' ||
                       isSearching === 'none' && 'invisible'}
                       placeholder = '–ü–æ–∏—Å–∫ –ø–æ —É—á–µ–Ω–∏–∫–∞–º –∏ –∫–ª–∞—Å—Å–∞–º'onChange = {e => doSearch(e)}/>

                <svg className = 'searchIcon' name = 'searchIcon' width="250px" height="277px" viewBox="0 0 250 277" version="1.1"
                     onClick = {e => handleSearchActivating(e)}>
                    <g id="Logo" stroke="none" stroke-width="1" fill="none" fill-rule="evenodd">
                        <path className = {   isSearching === 'active' && 'activeStroke activeFill' ||
                        isSearching === 'inactive' && 'inactiveStroke inactiveFill' ||
                        isSearching === 'none' && 'inactiveStroke inactiveFill'}
                              d="M256.23536,245.633373 C256.240323,245.854751 256.242819,246.076789 256.242819,246.299457 C256.242819,261.211145 245.049938,273.299457 231.242819,273.299457 C217.4357,273.299457 206.242819,261.211145 206.242819,246.299457 C206.242819,246.076789 206.245315,245.854751 206.250278,245.633373 Z" id="Combined-Shape" transform="translate(231.242819, 259.466415) rotate(-40.000000) translate(-231.242819, -259.466415) "></path>
                        <rect id="Rectangle"
                              className = {   isSearching === 'active' && 'activeStroke activeFill' ||
                              isSearching === 'inactive' && 'inactiveStroke inactiveFill' ||
                              isSearching === 'none' && 'inactiveStroke inactiveFill'}
                              transform="translate(199.856726, 222.096267) rotate(-40.000000) translate(-199.856726, -222.096267) " x="174.856726" y="186.596267" width="50" height="71"></rect>
                        <circle id="Oval"
                                className = {   isSearching === 'active' && 'activeStroke' ||
                                isSearching === 'inactive' && 'inactiveStroke' ||
                                isSearching === 'none' && 'inactiveStroke'}
                                stroke-width="50" cx="125" cy="125" r="100"></circle>
                    </g>
                </svg>

                <svg className = 'sortIcon' id = 'sortIcon' onClick = {e => doSort(e)} width="378px" height="282px" viewBox="0 0 378 282" version="1.1">
                    <g id="Logo" stroke="none" stroke-width="1" fill="none" fill-rule="evenodd">
