import React, {useContext, useEffect, useState} from 'react';
import './Appearence/App.css';
import './Appearence/Auth.css';
import {useHttp} from "../Hooks/http.hook";
import {useMessage} from "../Hooks/message.hook";
import {AuthContext} from "./Context/AuthContext";
import pic from "../Images/INTRO.svg";


export const Auth = () => {
    const auth = useContext(AuthContext)
    const {loading, request, error, clearError} = useHttp();
    const [form, setForm] = useState({ login: '', password: '' })
    const message = useMessage()

    useEffect(() => {
        message(error)
        clearError()
        // document.body.style = 'transition-duration: .7s; transition-timing-function: ease-in-out; background: white;'
    }, [error, message, clearError])

     const changeHandler = event => {
         setForm({...form, [event.target.name]: event.target.value})
     }

     const registerHandler = async () => {
         try {
          const data = await request('api/auth/register', 'POST', {...form})
             console.log(data.message)
         }
         catch (e) {}
     }

    const loginHandler = async () => {
        try {
            const data = await request('api/auth/login', 'POST', {...form})
            console.log(data.message)
            auth.login(data.token, data.userID, data.userType, data.name, data.surname, data.username)

            // OPTIONAL
            // console.log('USER TYPE: ', data.userType)
            // console.log('USER NAME: ', data.name)
            // console.log('USER SURNAME: ', data.surname)
        }
        catch (e) {}
    }


      return (
          <div className = 'authPage'>
              <div id = 'intropic'>
                  {/*<img src = {pic}/>*/}
                  <svg width="660px" height="330px" viewBox="0 0 660 330" version="1.1">
