                              <path d="M395,5 C408.807119,5 420,16.1928813 420,30 L420.000134,47.3781418 C434.708772,38.8696847 451.785698,34 470,34 C524.67619,34 569.103524,77.8805717 569.986603,132.346317 L570,134 L570,134 C570,147.807119 558.807119,159 545,159 C531.192881,159 520,147.807119 520,134 L520,133.999 L519.990199,133.99997 C519.457521,106.847326 497.280119,85 470,85 C442.719881,85 420.542479,106.847326 420.009801,133.99997 L420,133.999 L420,134 C420,147.807119 408.807119,159 395,159 C381.192881,159 370,147.807119 370,134 L370,30 C370,16.1928813 381.192881,5 395,5 Z" id="Combined-Shape"></path>
                          </g>
                          <g id="T-Copy-13" transform="translate(403.000000, 165.500000) rotate(180.000000) translate(-403.000000, -165.500000) translate(303.000000, 10.000000)" fill="#FFFFFF">
                              <path d="M168.488652,54.3137959 C178.835915,52.0217387 189.157553,58.2669807 191.928532,68.4964023 L200,98.2932536 L200,98.2932536 L200,285.218313 C200,299.025431 188.807119,310.218313 175,310.218313 C161.192881,310.218313 150,299.025431 150,285.218313 L150,77.3461013 C150,66.2832423 157.687613,56.7063702 168.488652,54.3137959 Z" id="Rectangle-Copy-18"></path>
                              <path d="M100,-5.68434189e-14 C154.67619,-5.68434189e-14 199.103524,43.8805717 199.986603,98.3463172 L200,100 L149.864893,99.9998332 C147.969541,72.044426 126.368452,50 100,50 C73.6315483,50 52.0304592,72.044426 50.1351074,99.9998332 L0,100 C0,44.771525 44.771525,-5.68434189e-14 100,-5.68434189e-14 Z" id="Combined-Shape"></path>
                              <rect id="Rectangle-Copy-15" x="0" y="75" width="50" height="146" rx="25"></rect>
                          </g>
                          <rect id="Rectangle-Copy-73" fill="#FFFFFF" x="8" y="71" width="50" height="250" rx="25"></rect>
                          <rect id="Rectangle-Copy-72" fill="#FFFFFF" transform="translate(345.500000, 100.500000) rotate(90.000000) translate(-345.500000, -100.500000) " x="320.5" y="38" width="50" height="125" rx="25"></rect>
                          <circle id="Oval-Copy-31" fill="#FFFFFF" cx="73.5" cy="43.5" r="27.5"></circle>
                          <circle id="Oval-Copy-30" fill="#FFFFFF" cx="553" cy="205" r="25"></circle>
                          <rect id="Rectangle-Copy-71" fill="#FFFFFF" x="8" y="71" width="50" height="250" rx="25"></rect>
                          <rect id="Rectangle-Copy-70" fill="#FFFFFF" transform="translate(345.500000, 100.500000) rotate(90.000000) translate(-345.500000, -100.500000) " x="320.5" y="38" width="50" height="125" rx="25"></rect>
                      </g>
                  </svg>
              </div>
              <div className = 'authFormAndButton'>
                  <div className = 'authform'>
                      <input type='text' id = 'login' name='login' placeholder='Логин' onChange={changeHandler} value = {form.login}/>
                      <input type='password' id = 'password' name='password' placeholder='Пароль' onChange={changeHandler} value = {form.password}/>
                  </div>
                  {/*<input type='button' className = 'loginbtn' value='reg' onClick = {registerHandler} disabled={loading}/>*/}
                  <input type='button' className = 'loginbtn' onClick = {loginHandler} disabled={loading}/>
              </div>
          </div>
      )
}

export default Auth
