import React, {useCallback, useContext, useEffect, useState} from 'react'
import './Appearence/index.css'
import './Appearence/AddTest.css'
import {AuthContext} from "./Context/AuthContext";
import {Translit} from "./Context/Translit";
import {useHttp} from "../Hooks/http.hook";
import {ReactComponent as AddIcon} from "./Appearence/Images/AddIcon.svg"
import {ReactComponent as CancelIcon} from "./Appearence/Images/CancelIcon.svg"


export const AddTest = () => {

    const {token, userID} = useContext(AuthContext)
    const {createShortLink} = useContext(Translit)
    const {loading, request, error, clearError} = useHttp();

    const [drafts, setDrafts] = useState()
    const [fromDrafts, setFromDrafts] = useState(false)

    const [testData, setTestData] = useState({
        name: '',
        link: '',
        shortDescription: '',
        description: '',
        scales: [{
            name: '' ,
            k: '',
            intervals: [{
                name: '',
                description: '',
                min: '',
                max: '',
                important: false
            }]
        }
        ],
        questions: ['']
    })

    const fetchDrafts = useCallback(async() => {
        try {
            const fetched = await request('addtest/drafts', 'GET', null, {
                Authorization: `Bearer ${token}`
            })
            setDrafts(fetched)
        }
        catch (e) {}
    }, [token, request])

    const saveTestDraft = async() => {
        console.log(testData)
        const data = await request('addtest/savedraft', 'POST', {...testData, date: new Date()})
        console.log(data.message)
        if (data.message === 'Draft is saved') alert(data.message)
    }


    // const handleSelect = e => {
    //     e.preventDefault()
    //     setFromDrafts(!fromDrafts)
    // }

    const chooseDraft = e => {
        e.preventDefault()
        setTestData(drafts[e.target.value])
    }

    const changeHandler = e => {
        e.preventDefault()
        if (e.target.name === 'name') {
            let link = createShortLink(e.target.value)
            setTestData({...testData, [e.target.name]: e.target.value, link})
        }
        else setTestData({...testData, [e.target.name]: e.target.value})
        console.log(testData)
    }

    const handleAddingScale = e => {
        e.preventDefault()
        const scales = testData.scales
        scales.push({
            name: '' ,
            k: '',
            intervals: [{
                name: '',
                description: '',
                min: '',
                max: '',
                important: false
            }]
        })
        setTestData({...testData, scales})
    }

    const handleDeletingScale = (e, i) => {
        e.preventDefault()
        let scales = testData.scales
        scales.splice(i, 1)
        setTestData({...testData, scales})
        if (scales.length == 0) handleAddingScale(e)
    }

    const handleAddingInterval = (e, i) => {
        e.preventDefault()
        let scales = testData.scales
        let intervals = scales[i]['intervals']
        intervals.push({
                name: '',
                description: '',
                min: '',
                max: '',
                important: false
        })
        scales['intervals'] = intervals
        setTestData({...testData, scales})
    }

    const handleDeletingInterval = (e, i, j) => {
        e.preventDefault()
        let scales = testData.scales
        let intervals = scales[i]['intervals']
        intervals.splice(j, 1)
        scales['intervals'] = intervals
        setTestData({...testData, scales})
        if (intervals.length == 0) handleAddingInterval(e, i)
    }

    const handleAddingQuestion = e => {
        e.preventDefault()
        const questions = testData.questions
        questions.push('')
        setTestData({...testData, questions})
    }

    const handleDeletingQuestion = (e, i) => {
        e.preventDefault()
        let questions = testData.questions
        questions.splice(i, 1)
        setTestData({...testData, questions})
        if (questions.length == 0) handleAddingQuestion(e)
    }

    const changeScaleHandler = (e, i) => {
        let scales = testData.scales
        scales[i][e.target.name] = e.target.value
        setTestData({...testData, scales})
    }

    const changeIntervalHandler = (e, i, j) => {
        let scales = testData.scales
        let intervals = scales[i]['intervals']

        if (e.target.name === 'important')
            intervals[j]['important'] = !intervals[j]['important']
        else
            intervals[j][e.target.name] = e.target.value

        scales[i]['intervals'] = intervals
        setTestData({...testData, scales})
    }


    useEffect(() => {
        document.body.style = 'transition-duration: 1s; transition-timing-function: ease-in-out; background: #FFFFFF;'
        fetchDrafts()
    }, [])

    useEffect(() => {
        if (fromDrafts && !drafts)
            fetchDrafts()
        else {
            if (testData) saveTestDraft()
            setTestData({
                name: '',
                link: '',
                shortDescription: '',
                description: '',
                scales: [{
                    name: '' ,
                    k: '',
                    intervals: [{
                        name: '',
                        description: '',
                        min: '',
                        max: '',
                        important: false
                    }]
                }
                ],
                questions: ['']
            })
        }
    }, [fromDrafts])

    useEffect(() => {
        // setInterval(() => {console.log('hi')}, 1000)
    }, [])


    return (
        // <div className = 'soft_appearing'>
        <div className = 'addingTest'>
            <h1>Добавить тест</h1>
            <select onChange = {() => setFromDrafts(!fromDrafts)}>
                <option defaultValue value = 'new'>вручную</option>
                <option value = 'draft'>из черновиков</option>
            </select>

            {
                fromDrafts && drafts &&
                 <select defaultValue = '' onChange = {e => chooseDraft(e)}>
                     <option value = ''>Выберите черновик</option>
                    {drafts.map((draft, i) => {
                        return (
                            <option key = {i} value = {i}>{draft.name}</option>
                        )
                    })}
                 </select>
            }

            <h2>Название методики</h2>
            <input type = 'text' name = 'name' value = {testData.name} placeholder = 'Название методики' onChange = {e => changeHandler(e)}/>

            <h3>Краткая ссылка</h3>
            <input type = 'text' name = 'link' value = {testData.link} placeholder = 'Краткая ссылка' onChange = {e => changeHandler(e)}/>

            <h2>Краткое описание</h2>
            <textarea name = 'shortDescription' value = {testData.shortDescription} placeholder = 'Краткое описание методики' onChange = {e => changeHandler(e)}/>

            <h2>Описание</h2>
            <textarea name = 'description' value = {testData.description} placeholder = 'Описание методики' onChange = {e => changeHandler(e)}/>

            <h2>Шкалы</h2>
            <p>Шкалы – это бываоыаыдйуж ваьыладыыьвда ывалы. Вдлывалад жавдыаж</p>
            <p>Шкальные интервалы – это бываоыаыдйуж ваьыладыыьвда ывалы. Вдлывалад жавдыаж</p>

            <div className = 'scales'>
            {
                testData.scales.map((scale, i) => {
                    return(
                        <div className = 'scaleBox'>
                            <CancelIcon id = 'scaleDeletingIcon' onClick = { e => handleDeletingScale(e, i) }/>
                        <div className = 'scale soft_appearing' key = {i}>
                            <input type = 'text' name = 'name' placeholder ={'Название шкалы ' + (i + 1)} value = {scale.name} onChange = { e => changeScaleHandler(e, i) }/>
                            <p>На сколько будут умножаться баллы?</p>
                            <input type = 'number' name = 'k' placeholder = 'Коэффициент' min = '1' value = {scale.k} onChange = { e => changeScaleHandler(e, i) }/>

                        </div>
                            <div className = 'intervals'>
                                <h3>Шкальные интервалы</h3>
                            {
                                scale.intervals.map((interval, j) => {
                                    return (
                                        <div className = 'interval soft_appearing' key = {j}>
                                            <CancelIcon onClick = { e => handleDeletingInterval(e, i, j) }/>
                                            <input type = 'text' name = 'name' value = {interval.name} placeholder = {'Интервал '+ (j + 1)} onChange = { e => changeIntervalHandler(e, i, j) }/>
                                            <p>Интервал отображает выраженный результат по шкале?</p>
                                            <input type = 'checkbox' name = 'important' checked = {interval.important} onChange = { e => changeIntervalHandler(e, i, j) }/>
                                            <textarea name = 'description' value = {interval.description} placeholder = 'Комментарий' onChange = { e => changeIntervalHandler(e, i, j) }/>
                                            <input type = 'number' name = 'min' value = {interval.min} placeholder = 'Минимальное кол-во баллов' onChange = { e => changeIntervalHandler(e, i, j) }/>
                                            <input type = 'number' name = 'max' value = {interval.max} placeholder = 'Максимальное  кол-во баллов' onChange = { e => changeIntervalHandler(e, i, j) }/>
                                        </div>)})
                            }
                                <AddIcon title = 'Новый интервал' onClick = { e => handleAddingInterval(e, i) }/>
                            </div>
                        </div>)
                })
            }
            </div>
            {/*<input type = 'button' value = '+' onClick = { e => handleAddingScale(e) }/>*/}
            <AddIcon title = 'Новая шкала' onClick = { e => handleAddingScale(e) }/>

            {/*<h2>Вопросы</h2>*/}
            {/*<div className = 'questionsAdd'>*/}
            {/*{*/}
            {/*    testData.questions.map((question, i) => {*/}
            {/*        return(*/}
            {/*            <div className = 'questionAdd' key = {i}>*/}
            {/*                <input type = 'text' name = {'q' + i} placeholder = {'Текст вопроса ' + i} value = {testData.questions[i]} onChange = { e => changeScaleHandler(e, i) }/>*/}
            {/*                <input type = 'button' value = 'x' onClick = { e => handleDeletingQuestion(e, i) }/>*/}
            {/*                /!*<input type = 'number' name = 'k' placeholder = 'Коэффициент' min = '1' value = {scale.k} onChange = { e => changeScaleHandler(e, i) }/>*!/*/}
            {/*                /!*<input type = 'button' value = 'x' onClick = { e => handleDeletingScale(e, i) }/>*!/*/}
            {/*            </div>)*/}
            {/*    })*/}
            {/*}*/}
            {/*</div>*/}
            {/*<AddIcon onClick = { e => handleAddingQuestion(e) }/>*/}

            <div className = 'registerbtn' value = 'Ок' onClick = {() => saveTestDraft()}></div>


        </div>


    )
}

export default AddTest;
