import React, {useCallback, useContext, useEffect, useState} from 'react';
import './Appearence/index.css'
import './Appearence/Auth.css';
import {useHttp} from "../Hooks/http.hook";
import {AuthContext} from "./Context/AuthContext";
import {NavLink} from "react-router-dom";


export const Students = () => {

    const {loading, request, error, clearError} = useHttp();

    const [form, setForm] = useState({
        login: '',
        password: '',
        type: '',
        name: '',
        surname: '',
        grade: null,
        letter: '',
        birthdate: ''
    })
    const [students, setStudents] = useState([])
    const {token} = useContext(AuthContext)

    const changeHandler = event => {
        setForm({...form, [event.target.name]: event.target.value})
    }

    const registerHandler = async () => {
        try {
            const data = await request('api/students/create', 'POST', {...form})
            console.log(data.message)
            if (data.message === 'User\'ve created'){
                setForm({
                    login: '',
                    password: '',
                    type: '',
                    name: '',
                    surname: '',
                    grade: null,
                    letter: '',
                    birthdate: ''
                })
            }
        }
        catch (e) {}
    }

    const fetchStudents = useCallback(async () => {
        try {
            const fetched = await request('/api/students', 'GET', null, {
                Authorization: `Bearer ${token}`
            })
            setStudents(fetched)
        } catch (e) {}
    }, [token, request])

    useEffect(() => {
        fetchStudents()
        document.body.style = 'transition-duration: .5s; transition-timing-function: ease-in-out; background: white;'
    }, [fetchStudents])

    return(
        <div>
            <h1>Ученики</h1>
            {/*<div className = 'authform'>*/}
            {/*    <input type='text' id = 'login' name='login' placeholder='Логин' onChange={changeHandler} value = {form.login}/>*/}
            {/*    <input type='text' id = 'password' name='password' placeholder='Пароль' onChange={changeHandler} value = {form.password}/>*/}
            {/*    <input type='text' id = 'type' name='type' placeholder='Тип' onChange={changeHandler} value = {form.type}/>*/}
            {/*    <input type='text' id = 'name' name='name' placeholder='Имя' onChange={changeHandler} value = {form.name}/>*/}
            {/*    <input type='text' id = 'surname' name='surname' placeholder='Фамилия' onChange={changeHandler} value = {form.surname}/>*/}
            {/*    { form.type === 'u' &&*/}
            {/*        <input type='text' id = 'grade' name='grade' placeholder='Класс' onChange={changeHandler} value = {form.grade}/>}*/}
            {/*    { form.type === 'u' &&*/}
            {/*        <input type='text' id = 'letter' name='letter' placeholder='Буква' onChange={changeHandler} value = {form.letter}/>}*/}
            {/*    { students.map((student) => {*/}
            {/*        return (*/}
            {/*            <div key = {student._id} className = 'authform'>*/}
            {/*                /!*<h2>{student._id}</h2>*!/*/}
            {/*                <h2>{student.grade}{student.letter}</h2>*/}
            {/*                <h2>{student.name} {student.surname}</h2>*/}
            {/*            </div>*/}
            {/*        )*/}
            {/*    }) }*/}
            {/*</div>*/}
            {/*<input type='button' className = 'loginbtn' value='reg' onClick = {registerHandler} disabled={loading}/>*/}
            <div id = 'testsCollection' className = 'tests'>
                { students.map((student, index) => {
                    return (
                        <NavLink to = {'/profile/' + student.user} key = {index} className = 'testItem soft_appearing'>
                            <p className = 'testName'>{student.name} {student.surname} {student.birthdate}</p>
                        </NavLink>
                    )
                })}
            </div>
        </div>
    )
}

export default Students
