import React from "react";
import {NavLink} from "react-router-dom";
import './Appearence/index.css'
import {useHistory} from "react-router-dom";


export const NavigationBar = () => {

    const history = useHistory()
    const handleClick = (e) => {
        const elements = document.getElementById('hey').childNodes
        elements.forEach(element => makeSmaller(element))
        makeBigger(e.target)
        e.preventDefault()
        history.push(`/${e.target.id}`)
    }
    const makeSmaller = (e) => {
        e.target.style.height = '80px'
        e.target.style.width = '80px'
    }
    const makeBigger = (e) => {
        e.target.style.height = '100px'
        e.target.style.width = '100px'
    }

    return (
        <div id = 'hey'>
            <div className='protocircle' id = 'profile' onClick = {handleClick}/>
            <div className='protocircle' id = 'students' onClick = {handleClick}/>
            <div className='protocircle' id = 'tests' onClick = {handleClick}/>
        </div>
    )
}
    export default NavigationBar
