import React from "react";
import {NavLink} from "react-router-dom";
import './Appearence/index.css'
import {useHistory} from "react-router-dom";


export const NavigationBar = () => {
    const history = useHistory()
    const handleClick = (e) => {
        switch (e.target.id){
            case 'profile': e.target.profile.style.background = 'red'
            case 'students': e.target.students.style.background = 'red'
            case 'tests': e.target.tests.style.background = 'red'
        }

        e.preventDefault()
        history.push(`/${e.target.id}`)
    }
    const makeSmaller = (e) => {
        e.target.style.height = '100px'
        e.target.style.width = '100px'
    }
    const makeBigger = (e) => {
        e.target.style.height = '100px'
        e.target.style.width = '100px'
    }

    return (
        <div>
            <div className='protocircle' id = 'profile' onClick = {handleClick}/>
            <div className='protocircle' id = 'students' onClick = {handleClick}/>
            <div className='protocircle' id = 'tests' onClick = {handleClick}/>
        </div>
    )
}
    export default NavigationBar
