import React, {useCallback, useContext, useEffect, useState} from 'react'
import './Appearence/index.css'
import './Appearence/TestPage.css'
import {AuthContext} from "./Context/AuthContext";
import {useHttp} from "../Hooks/http.hook";
import {useParams} from 'react-router-dom'

export const TestPage = () => {

    const [test, setTest] = useState({ name: '', description: '', shortDescription: '', questions: ''})
    const [questions, setQuestions] = useState({})
    const {token, userID} = useContext(AuthContext)
    const [answers, setAnswers] = useState({userID: userID})
    const {loading, request, error, clearError} = useHttp();
    const link = useParams().id

    const [content, setContent] = useState('description')
    const [index, setIndex] = useState(0)
    const [goNext, setGoNext] = useState(false)

    const fetchTestData = useCallback(async () => {
        try {
            const fetched = await request(`/api/tests/${link}`, 'GET', null, {
                Authorization: `Bearer ${token}`
            })
            // setTest({name: fetched.tests.name, description: fetched.tests.description, shortDescription: fetched.tests.shortDescription, questions: fetched.tests.questions})
            console.log(fetched)
            setQuestions(fetched.questions)
            // setAnswers({...answers, testID: fetched.questions.testID})
        }
        catch (e) {}
    }, [token, request])

    const showTest = e => {
        e.preventDefault()
        setContent('testStartPage')
    }

    const yesHandler = e => {
        e.preventDefault()
        document.getElementById('y' + index).style = 'transform: scale(1.2)'
        document.getElementById('n' + index).style = 'transform: scale(1)'
        setAnswers({...answers, ["a" + e.target.id]: true })
        setGoNext(true)
    }

    const noHandler = e => {
        e.preventDefault()
        document.getElementById('y' + index).style = 'transform: scale(1)'
        document.getElementById('n' + index).style = 'transform: scale(1.2)'
        setAnswers({...answers, ["a" + e.target.id]: false })
        setGoNext(true)
    }

    const next = e => {
        e.preventDefault()
        if (goNext){
            if (index < test.questions)setIndex(index + 1)
            document.getElementById('y' + index).style = 'transform: scale(1)'
            document.getElementById('n' + index).style = 'transform: scale(1)'
            try { document.getElementById('next' + index).style = 'transform: scale(1.2)' } catch (e) {}
            try { document.getElementById('next' + index).style = 'transform: scale(1)' } catch (e) {}
            try { document.getElementById('back' + index).style = 'transform: scale(1)' } catch (e) {}
            setGoNext(false)
        }
    }

    const back = e => {
        e.preventDefault()
        if (index > 0) setIndex(index - 1)
        document.getElementById('y' + index).style = 'transform: scale(1)'
        document.getElementById('n' + index).style = 'transform: scale(1)'
        try { document.getElementById('next' + index).style = 'transform: scale(1)' } catch (e) {}
        try { document.getElementById('back' + index).style = 'transform: scale(1.2)' } catch (e) {}
        setGoNext(false)
    }

    const saveAnswers = async (e) => {
        e.preventDefault()
        try {
            const data = await request(`${link}/save`, 'POST', {...answers}, {
                Authorization: `Bearer ${token}`
            })
            console.log(data)
        }
        catch (e) {}
    }

    useEffect(() => {
        fetchTestData()
        if (content === 'testStartPage') document.body.style = 'transition-duration: 1s; transition-timing-function: ease-in-out; background: #E6EBC3;'
    }, [fetchTestData, content])


    return (
        <div>
            { content === 'description' &&
                <div className = 'testInfo'>
                    <h1 className = 'bigTestName'>{test.name}</h1>
                    <p className='testDescription'>{test.shortDescription}
                    <br/>{test.description}</p>
                    <input type='button' className = 'redbtn'  value = '' disabled={loading} onClick = {showTest} />
                </div>
            }

            { content === 'testStartPage' &&
                <div>
                {
                    // Object.keys(questions).map((key, index) =>
                    //     key.includes('q') &&
                    //     <div key = {index} id = {'d' + index} className = 'questionCard' >
                    //         <h1>{index}/{test.questions}</h1>
                    //         <p value={key}>{ questions[key] } </p>
                    //         <input type='button' className = 'yesbtn'  value = 'да' id = {'y'+index} disabled={loading} onClick={yesHandler}/>
                    //         <input type='button' className = 'nobtn'  value = 'нет' id = {'n'+index} disabled={loading} onClick={noHandler}/>
                    //     </div>
                    // )
                    // Object.keys(questions)[index]
                    // key.includes('q') &&
                    <div id = {'d' + index} className = 'questionCard' >
                        <h1>{index + 1}/{test.questions}</h1>
                        <p>{questions['q' + (index + 1)]}</p>
                        {
                            index > 0 &&
                            <input type='button' className = 'nobtn'  value = 'назад' id = {'back'+index} disabled={loading} onClick={e => back(e)}/>
                        }
                        <input type='button' className = 'yesbtn'  value = 'да' id = {'y'+index} disabled={loading} onClick={e => yesHandler(e)}/>
                        <input type='button' className = 'nobtn'  value = 'нет' id = {'n'+index} disabled={loading} onClick={e => noHandler(e)}/>
                        {
                            index < test.questions - 1 &&
                            <input type='button' className = 'nobtn'  value = 'дальше' id = {'next'+index} disabled={loading} onClick={e => next(e)}/>
                        }
                    </div>
                }
                    {
                        // index == test.questions - 1 &&
                        <input type='button' className = 'yesbtn'  value = 'результат' disabled={loading} onClick={e => saveAnswers(e)}/>
                    }
                </div>
            }
        </div>


    )
}

export default TestPage;
